import React from 'react';
import './SortingVisualizer.css';
import { getBubbleSortAnimation } from '../SortingAlgorithms/SortingAlgorithms.jsx';


const DEFAUL_LENGTH = 20;

const RAND_MIN = 5;

const RAND_MAX = 300;

const ANIMATE_SPEED = 5;

const CONFIRM_COLOR = '#27AE60';

// const PRIMARY_COLOR = "#3537F2";

const SECONDARY_COLOR = "#FFBF00";

const ORIGINAL_COLOR = "#6495ED";


export default class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            array: [],
            length: DEFAUL_LENGTH,
        };

        this.handleChange = this.handleChange.bind(this);
        this.handleSubmit = this.handleSubmit.bind(this);
    }

    componentDidMount() {
        this.resetArray();
    }

    componentWillUnmount() {
        this.setState = () => false;
    }

    mergeSort(){
        for (let i = 0; i < 10; i++) {
            setTimeout(()=>{this.resetArray()}, ANIMATE_SPEED * i)
            // console.log(this.state.array)
        }
    }

    bubbleSort(){
        const animations = getBubbleSortAnimation(this.state.array)

        for (let i = 0; i < animations.length; i++) {
            const arrayBars = document.getElementsByClassName('array-bar');
            const [barOneIdx, barTwoIdx, oneVal, twoVal, finish] = animations[i];

            if (finish === 1) {
                const barOneStyle = arrayBars[barOneIdx].style;
                setTimeout(() => {
                    barOneStyle.backgroundColor = CONFIRM_COLOR;
                }, i * ANIMATE_SPEED);
            }
            else {
                const barOneStyle = arrayBars[barOneIdx].style;
                const barTwoStyle = arrayBars[barTwoIdx].style;

                setTimeout(() => {
                    barOneStyle.backgroundColor = ORIGINAL_COLOR;
                    barTwoStyle.backgroundColor = SECONDARY_COLOR;

                    if (oneVal > twoVal) {
                        barOneStyle.height = `${twoVal}px`;
                        barTwoStyle.height = `${oneVal}px`;
                    }
                }, i * ANIMATE_SPEED);

            }
        }
    }

    quickSort(){}

    resetArray() {
        const array = []
        for (let i = 0; i < this.state.length; i++) {
            array.push(randomNum(RAND_MIN, RAND_MAX));
        }
        this.setState({array: array});
        this.resetColor();
    }

    resetColor() {
        const arrayBars = document.getElementsByClassName('array-bar');
        for (let i = 0; i < arrayBars.length; i++) {
            arrayBars[i].style.backgroundColor = ORIGINAL_COLOR;
        }
    }

    handleChange(event) {
        this.setState({length: event.target.value});
    }
    handleSubmit(event) {
        event.preventDefault();
        console.log(this.state)
        this.resetArray();
    }

    render() {
        const {array} = this.state

        return (
            <div className="container">
                <div className="nav-bar">
                    <div className="logo text">Sorting Visualizer</div>
                    <div className="setting-container">
                        <form className="form" onSubmit={this.handleSubmit}>
                            <input style={{width:"100%"}} type="text" value={this.state.length} onChange={this.handleChange}/>
                            <input className="btn text" type="submit" value="Confirm"/>
                        </form>
                    </div>
                    <div className="algo-container">
                        <button className="btn text" onClick={() => this.resetArray()}>New Array</button>
                        <button className="btn text" onClick={() => this.mergeSort()}>Merge Sort</button>
                        <button className="btn text" onClick={() => this.bubbleSort()}>Bubble Sort</button>
                        <button className="btn text" onClick={() => this.quickSort()}>Quick Sort</button>
                    </div>
                </div>
                <div className="array-container">
                    {array.map((value, idx) => (
                        <div
                            className="array-bar"
                            key={idx}
                            style={{
                                height: `${value}px`,
                                backgroundColor: ORIGINAL_COLOR,
                            }}>
                        </div>
                    ))}
                </div>
            </div>
        );
    }

}

function randomNum(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}
