export function getBubbleSortAnimation(array) {
    console.log('Start Bubble Sort!');
    console.log(array);
    array = bubbleSort(array);
    console.log(array);
    console.log(arraySorted(array));

}

function bubbleSort(array) {
    let arr_len = array.length;

    for (let i = 0; i < arr_len; i++) {
        for (let j = 0; j < arr_len - i; j++) {
            if (array[j] > arr[j + 1]) {
                let tmp = array[j];
                array[j] = array[j + 1];
                array[j + 1] = tmp;
            }
        }
    }
    return array;
}


function arraySorted(array) {
    if (array.length <= 1) { return true; }

    let pre = array[0];

    for (let i = 1; i < array.length; i++){
        if (array[i] < pre) {return false;}
        pre = array[i];
    }
    return true;
}
