import React from 'react';
import './SortingVisualizer.css';
import { getBubbleSortAnimation } from '../SortingAlgorithms/SortingAlgorithms.jsx';


const ARRAYNUM = 10;

const RAND_MIN = 5;

const RAND_MAX = 200;

const ANIMATE_SPEED = 1000;

const CONFIRM_COLOR = '#27AE60';

const PRIMARY_COLOR = "#3537F2";

const SECONDARY_COLOR = "#FFBF00";

const ORIGINAL_COLOR = "#6495ED";


export default class SortingVisualizer extends React.Component {
    constructor(props) {
        super(props);

        this.state = {
            array: []
        };
    }

    componentDidMount() {
        this.resetArray();
    }

    componentWillUnmount() {
        this.setState = () => false;
    }

    mergeSort(){
        for (let i = 0; i < 10; i++) {
            setTimeout(()=>{this.resetArray()}, ANIMATE_SPEED * i)
            // console.log(this.state.array)
        }
    }

    bubbleSort(){
        const animations = getBubbleSortAnimation(this.state.array)

        for (let i = 0; i < animations.length; i++) {
            const arrayBars = document.getElementsByClassName('array-bar');
            const [barOneIdx, barTwoIdx, oneVal, twoVal, finish] = animations[i];
            let preStyle = arrayBars[0].style;

            if (finish === 1) {
                const barOneStyle = arrayBars[barOneIdx].style;
                setTimeout(() => {
                    barOneStyle.backgroundColor = CONFIRM_COLOR;
                }, i * ANIMATE_SPEED);
            }
            else {
                setTimeout(() => {
                const barPreStyle = barOneIdx != 0 ? arrayBars[barOneIdx - 1].style : null;
                const barOneStyle = arrayBars[barOneIdx].style;
                const barTwoStyle = arrayBars[barTwoIdx].style;

                    if (barPreStyle) {
                        barPreStyle.backgroundColor = ORIGINAL_COLOR;
                    }
                    barOneStyle.backgroundColor = PRIMARY_COLOR;
                    barTwoStyle.backgroundColor = SECONDARY_COLOR;

                    if (oneVal > twoVal) {
                        barOneStyle.height = `${twoVal}px`;
                        barTwoStyle.height = `${oneVal}px`;
                    }
                }, i * ANIMATE_SPEED);

            }
        }
    }

    quickSort(){}

    resetArray() {
        const array = []
        for (let i = 0; i < ARRAYNUM; i++) {
            array.push(randomNum(RAND_MIN, RAND_MAX));
        }
        this.setState({array: array});
        this.resetColor();
    }

    resetColor() {
        const arrayBars = document.getElementsByClassName('array-bar');
        for (let i = 0; i < arrayBars.length; i++) {
            arrayBars[i].style.backgroundColor = ORIGINAL_COLOR;
        }
    }

    render() {
        const {array} = this.state

        return (
            <div className="container">
                <div className="array-container">
                    {array.map((value, idx) => (
                        <div
                            className="array-bar"
                            key={idx}
                            style={{
                                height: `${value}px`,
                                backgroundColor: ORIGINAL_COLOR,
                            }}>
                        </div>
                    ))}
                </div>
                <div className="nav-bar">
                    <button onClick={() => this.resetArray()}>New Array</button>
                    <button >Array Length</button>
                    <button onClick={() => this.mergeSort()}>Merge Sort</button>
                    <button onClick={() => this.bubbleSort()}>Bubble Sort</button>
                    <button onClick={() => this.quickSort()}>Quick Sort</button>
                </div>
            </div>
        );
    }

}

function randomNum(min, max) {
    return Math.floor(Math.random() * (max - min + 1) + min);
}
